/**
 * This is an object of callbacks that are safe to be called even if `callbackObjectGetter()` is nullish on that moment.
 *
 * In the case of the object is nullish, invoking the callbacks will do nothing.
 */
export declare const carefullCallbackObject: <CallbacksObject extends object, Callbacks extends keyof CallbacksObject>(callbackObjectGetter: () => CallbacksObject, callbackNames: readonly Callbacks[]) => Pick<CallbacksObject, Callbacks>;
